mapper.select:
SELECT vocation_id, vocation_category_id, enterprise_id, vocation_name, status, work_type, place, people_number, min_salary, max_salary, experience, education, profile, welfare, create_time, last_edit_time FROM tb_vocation
AND enterprise_id=#{enterpriseId}
AND vocation_id=#{vocationId}
AND vocation_category_id=#{vocationCategoryId}
AND vocation_name LIKE CONCAT(CONCAT('%', #{vocationName}), '%')
AND min_salary >= #{minSalary}
AND experience=#{experience}
AND education=#{education}
AND place LIKE CONCAT(CONCAT('%', #{place}), '%')
AND status=#{status}
ORDER BY status DESC , last_edit_time DESC

mapper.update:
UPDATE
tb_vocation
SET
status = ABS(status-1),
last_edit_time=#{lastEditTime}
WHERE
enterprise_id=#{enterpriseId}
AND
vocation_id = #{vocationId}
